package com.iu.loop;

public class Loop1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Start");
		
		//for(초기식;조건식;증감식){}
		for(int i=0; i<5; i++) {
			System.out.println("Hello World");
		}
		
		for(int i=0; i<10; i++) {
			System.out.println(i);
		}
		
		System.out.println("Finish");
	}

}
